/**
 * Class to handle configurations entities.
 * @type {*|Function}
 */
module.exports = Class.create({

    querier: null,

    constructor: function()
    {
        this.querier = new (require('local/entity/query'));

        this.querier.register('config', __url('config/load'), [ 'id' ]);
        this.querier.register('table_load', __url('config/fetch'), [ 'id' ]);
        this.querier.register('delete', __url('config/delete'), [ 'id', 'rowId' ]);
    },

    getQuerier: function()
    {
        return this.querier;
    }

});