/**
 * Class to handle configurations entities.
 * @type {*|Function}
 */
module.exports = Class.create({

    loadConfig: function(configId)
    {
        return this._createQuery(__url('config/load'), {
            id: configId
        });
    },

    loadTableData: function(configId)
    {
        return this._createQuery(__url('config/fetch'), {
            id: configId
        });
    },

    deleteTableData: function(configId, rowId)
    {
        return this._createQuery(__url('config/delete'), {
            id: configId,
            rowId: rowId
        });
    },

    _createQuery: function(url, data)
    {
        data = data || {};

        var obj = {
            submit: function(callback)
            {
                return $.get(url, data, callback);
            },
            with: function(key, value)
            {
                data[key] = value || '';
                return obj;
            }
        };

        return obj;
    }

});